import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Link } from 'react-router-dom';
import mapData from '../data/mapData.json';

const SiteModal = ({ site, onClose }) => (
    <motion.div
      initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}
      className="fixed inset-0 bg-black/60 z-50 flex items-center justify-center p-4"
      onClick={onClose}
    >
      <motion.div
        initial={{ scale: 0.8, y: 50 }} animate={{ scale: 1, y: 0 }} exit={{ scale: 0.8, y: 50 }}
        className="bg-white rounded-2xl shadow-2xl max-w-md w-full text-center p-8 relative"
        onClick={(e) => e.stopPropagation()}
      >
        <h2 className="text-3xl font-extrabold gradient-text">{site.name}</h2>
        <p className="mt-4 text-slate-600">{site.summary}</p>
        <div className="mt-8">
            {site.relatedQuest ? (
                <Link to="/quests" className="px-8 py-3 rounded-full bg-gradient-to-r from-orange-500 to-red-500 text-white font-bold hover:scale-105 transition-transform shadow-lg shadow-orange-500/20">
                    Go to Quest
                </Link>
            ) : (
                <p className="text-sm text-slate-500 italic">More quests related to this site are coming soon!</p>
            )}
        </div>
        <button onClick={onClose} className="mt-4 text-sm text-slate-500 hover:underline">
          Close
        </button>
      </motion.div>
    </motion.div>
);

export default function Explore() {
    const [selectedSite, setSelectedSite] = useState(null);

    return (
        <>
            <section>
                <div className="text-center">
                    <h2 className="text-4xl font-extrabold tracking-tight text-slate-900">The Map Room 🗺️</h2>
                    <p className="mt-2 text-lg text-slate-600">Click on a location to learn more and discover related quests.</p>
                </div>

                <div className="mt-8 p-4 bg-white/70 backdrop-blur-sm rounded-2xl border border-slate-200 shadow-lg relative">
                    {/* Camera/Lens Button - Moved to bottom right */}
                    <motion.button 
                        whileHover={{ scale: 1.1 }}
                        whileTap={{ scale: 0.9 }}
                        className="absolute bottom-4 right-4 z-20 w-16 h-16 bg-white/80 backdrop-blur-sm rounded-full shadow-lg flex items-center justify-center border border-slate-200"
                        title="Future Feature: Visual Search"
                    >
                        <svg className="w-8 h-8 text-teal-500" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}>
                            <path strokeLinecap="round" strokeLinejoin="round" d="M3 9a2 2 0 012-2h.93a2 2 0 001.664-.89l.812-1.22A2 2 0 0110.07 4h3.86a2 2 0 011.664.89l.812 1.22A2 2 0 0018.07 7H19a2 2 0 012 2v9a2 2 0 01-2 2H5a2 2 0 01-2-2V9z" />
                            <path strokeLinecap="round" strokeLinejoin="round" d="M15 13a3 3 0 11-6 0 3 3 0 016 0z" />
                        </svg>
                    </motion.button>

                    <div className="relative w-full max-w-4xl mx-auto aspect-[4/3]">
                        {/* SVG Map of India - Now just an outline */}
                        <svg className="w-full h-full" viewBox="0 0 450 500">
                            <path d="M211.2,5.2c-1.3-1-3.1-1.2-4.6-0.5c-1.4,0.6-2.4,2-2.4,3.5v22.3l-28.7,30.3c-2.8,3-3.9,7.2-2.8,11.1l21.4,75.2 c-15.6-1.5-33.1,3.4-45.1,15.4c-12,12-16.9,29.6-15.4,45.1L60.5,190.1c-3.9,1.1-7.1,4.3-8.2,8.2L12.1,351.4 c-1.6,5.8,1.3,11.9,7.1,13.5l140.7,39.2c1.8,0.5,3.6,0.5,5.4,0l140.7-39.2c5.8-1.6,8.7-7.7,7.1-13.5L272.8,200.1 c-1.1-3.9-4.3-7.1-8.2-8.2l-73-20.3c1.5-15.6-3.4-33.1-15.4-45.1c-12-12-29.6-16.9-45.1-15.4l21.4-75.2 c1.1-3.9-0.1-8.1-2.8-11.1l-28.7-30.3V8.2c0-1.5-1-2.9-2.4-3.5C214.3,3.9,212.5,4.2,211.2,5.2z" fill="transparent" stroke="#a1a1aa" strokeWidth="2"/>
                        </svg>

                        {/* Interactive Markers - Replaced with location icons */}
                        {mapData.sites.map(site => (
                            <motion.g
                                key={site.id}
                                initial={{ scale: 0, opacity: 0 }}
                                animate={{ scale: 1, opacity: 1 }}
                                transition={{ type: 'spring', delay: 0.5 + (Math.random() * 0.5) }}
                                whileHover={{ scale: 1.2, zIndex: 10 }}
                                className="cursor-pointer absolute"
                                style={{ 
                                    left: `${(site.coords.x / 450) * 100}%`, 
                                    top: `${(site.coords.y / 500) * 100}%`,
                                    transform: 'translate(-50%, -100%)' // Center the pin tip
                                }}
                                onClick={() => setSelectedSite(site)}
                            >
                                {/* Pulsing animation effect */}
                                <circle r="12" fill="#ef4444" opacity="0.5">
                                    <animate attributeName="r" from="12" to="24" dur="1.5s" begin="0s" repeatCount="indefinite"/>
                                    <animate attributeName="opacity" from="0.5" to="0" dur="1.5s" begin="0s" repeatCount="indefinite"/>
                                </circle>
                                {/* Location Pin SVG Icon */}
                                <svg x="-12" y="-24" width="24" height="24" viewBox="0 0 24 24" fill="#ef4444" stroke="white" strokeWidth="1.5">
                                    <path strokeLinecap="round" strokeLinejoin="round" d="M12 2C8.13 2 5 5.13 5 9c0 5.25 7 13 7 13s7-7.75 7-13c0-3.87-3.13-7-7-7zm0 9.5c-1.38 0-2.5-1.12-2.5-2.5s1.12-2.5 2.5-2.5 2.5 1.12 2.5 2.5-1.12 2.5-2.5 2.5z" />
                                </svg>
                                {/* Site Name Label */}
                                <text x="0" y="8" textAnchor="middle" className="font-bold text-sm fill-current text-slate-700 pointer-events-none">
                                    {site.name}
                                </text>
                            </motion.g>
                        ))}
                    </div>
                </div>
            </section>

            <AnimatePresence>
                {selectedSite && (
                    <SiteModal site={selectedSite} onClose={() => setSelectedSite(null)} />
                )}
            </AnimatePresence>
        </>
    );
}
