import React from 'react';
import { Link, NavLink as RouterNavLink, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const CustomNavLink = ({ to, children }) => {
  return (
    <RouterNavLink
      to={to}
      className={({ isActive }) =>
        `px-3 py-2 rounded-lg text-sm font-semibold transition-colors ${
          isActive ? "text-teal-600" : "text-slate-600 hover:text-teal-500"
        }`
      }
    >
      {children}
    </RouterNavLink>
  );
};

export default function Navbar() {
  const { logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <nav className="sticky top-0 z-50 bg-white/80 backdrop-blur-lg border-b border-slate-200 shadow-md">
      <div className="max-w-7xl mx-auto px-4 py-3 flex items-center justify-between">
        <Link to="/" className="text-2xl font-extrabold gradient-text">CulturoQuest</Link>

        <div className="hidden md:flex items-center space-x-2">
          {/* All links are now visible when logged in */}
          <CustomNavLink to="/">Home</CustomNavLink>
          <CustomNavLink to="/explore">Explore</CustomNavLink>
          <CustomNavLink to="/quests">Quests</CustomNavLink>
          <CustomNavLink to="/profile">Profile</CustomNavLink>
          <CustomNavLink to="/about">About</CustomNavLink>
          <div className="border-l border-slate-300 h-6 mx-2"></div>
          <button 
            onClick={handleLogout}
            className="px-4 py-2 rounded-lg text-sm font-semibold bg-red-500 text-white hover:bg-red-600 transition-colors"
          >
            Logout
          </button>
        </div>
      </div>
    </nav>
  );
}
