import React, { useState } from 'react';
import { Link } from 'react-router-dom';

export default function Register() {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });

  const { username, email, password } = formData;

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = e => {
    e.preventDefault();
    // This is where we'll connect to the backend later
    console.log('Form submitted', formData);
  };

  return (
    <div className="max-w-md mx-auto mt-10">
      <div className="text-center mb-8">
        <h2 className="text-4xl font-extrabold">Join the <span className="gradient-text">Adventure</span></h2>
        <p className="mt-2 text-slate-600">Create your account to start your quest!</p>
      </div>
      <form onSubmit={onSubmit} className="bg-white p-8 rounded-2xl shadow-md border border-slate-200 space-y-6">
        <div>
          <label className="block font-semibold text-slate-700 mb-1">Username</label>
          <input
            type="text"
            name="username"
            value={username}
            onChange={onChange}
            required
            className="w-full p-3 rounded-lg border-2 border-slate-200 focus:border-orange-500 focus:ring-0 transition"
            placeholder="e.g., HistoryExplorer"
          />
        </div>
        <div>
          <label className="block font-semibold text-slate-700 mb-1">Email</label>
          <input
            type="email"
            name="email"
            value={email}
            onChange={onChange}
            required
            className="w-full p-3 rounded-lg border-2 border-slate-200 focus:border-orange-500 focus:ring-0 transition"
            placeholder="you@example.com"
          />
        </div>
        <div>
          <label className="block font-semibold text-slate-700 mb-1">Password</label>
          <input
            type="password"
            name="password"
            value={password}
            onChange={onChange}
            required
            minLength="6"
            className="w-full p-3 rounded-lg border-2 border-slate-200 focus:border-orange-500 focus:ring-0 transition"
            placeholder="Minimum 6 characters"
          />
        </div>
        <button
          type="submit"
          className="w-full px-8 py-3 rounded-full bg-gradient-to-r from-orange-500 to-red-500 text-white font-bold hover:scale-105 transition-transform shadow-lg shadow-orange-500/20"
        >
          Create Account
        </button>
      </form>
      <p className="text-center mt-6 text-slate-600">
        Already have an account?{' '}
        <Link to="/login" className="font-semibold text-teal-600 hover:underline">
          Log In
        </Link>
      </p>
    </div>
  );
}
