import React, { createContext, useState, useContext } from 'react';

const GameContext = createContext(null);

export const useGame = () => useContext(GameContext);

export const GameProvider = ({ children }) => {
  const [questPoints, setQuestPoints] = useState(1000);
  const [ownedItems, setOwnedItems] = useState([]); // This will now store coupon IDs

  const addPoints = (amount) => {
    setQuestPoints(prevPoints => prevPoints + amount);
  };

  // Renamed for clarity, but the logic is the same
  const buyItem = (item) => {
    if (questPoints >= item.price && !ownedItems.includes(item.id)) {
      setQuestPoints(prevPoints => prevPoints - item.price);
      setOwnedItems(prevOwned => [...prevOwned, item.id]);
      return true; // Purchase successful
    }
    return false; // Not enough points or already owned
  };

  const value = { questPoints, addPoints, ownedItems, buyItem };

  return <GameContext.Provider value={value}>{children}</GameContext.Provider>;
};
