import React, { createContext, useState, useContext, useEffect } from 'react';
import stagesData from '../data/stagesData.json';
import mauryanSaga from '../data/mauryanEmpire.json';
import triviaSaga from '../data/culturalTrivia.json';

const GameContext = createContext(null);
export const useGame = () => useContext(GameContext);

const allSagasData = {
  "Ancient India: The Mauryan Age (BCE)": mauryanSaga,
  "Indian Culture 101": triviaSaga
};

const getQuestionIdsForSaga = (sagaTitle) => {
    const saga = allSagasData[sagaTitle];
    if (!saga) return [];
    let ids = [];
    Object.values(saga.games).forEach(game => {
        if (game.questionBank) {
            game.questionBank.forEach(qSet => {
                if (qSet.events) ids.push(...qSet.events.map(e => e.id));
                if (qSet.rulers) ids.push(...qSet.rulers.map(r => r.id));
                if (qSet.statements) ids.push(qSet.id);
                // This was missing for ArtisansEye and CulturalMatchup
                if (qSet.item) ids.push(qSet.id); 
                if (qSet.imageUrl) ids.push(qSet.id);
            });
        }
        if (game.riddles) ids.push(...game.riddles.map(r => r.id));
    });
    return [...new Set(ids)];
};

export const GameProvider = ({ children }) => {
  const [questPoints, setQuestPoints] = useState(1000);
  const [ownedItems, setOwnedItems] = useState([]);
  const [selectedAvatar, setSelectedAvatar] = useState(localStorage.getItem('userAvatar') || 'default');
  const [correctlyAnsweredQIDs, setCorrectlyAnsweredQIDs] = useState(new Set(['e1', 'e2', 'cm1', 'cm2'])); // Start with a few correct for demo

  const [userProgress, setUserProgress] = useState({
    completedStages: [],
    currentStage: stagesData.stages[0].id
  });

  useEffect(() => {
    const currentStage = stagesData.stages.find(s => s.id === userProgress.currentStage);
    if (!currentStage) return;
    const relatedSagaTitle = currentStage.relatedSaga;
    const allQuestionIdsInSaga = getQuestionIdsForSaga(relatedSagaTitle);
    if (allQuestionIdsInSaga.length === 0) return;
    const correctlyAnsweredInSaga = allQuestionIdsInSaga.filter(id => correctlyAnsweredQIDs.has(id));
    const masteryPercentage = (correctlyAnsweredInSaga.length / allQuestionIdsInSaga.length) * 100;

    if (masteryPercentage >= 60) {
      const currentStageIndex = stagesData.stages.findIndex(s => s.id === userProgress.currentStage);
      const nextStage = stagesData.stages[currentStageIndex + 1];
      if (nextStage && !userProgress.completedStages.includes(userProgress.currentStage)) {
        setUserProgress(prev => ({
          completedStages: [...prev.completedStages, prev.currentStage],
          currentStage: nextStage.id
        }));
      } else if (!nextStage && !userProgress.completedStages.includes(userProgress.currentStage)) {
        setUserProgress(prev => ({
          ...prev,
          completedStages: [...prev.completedStages, prev.currentStage],
        }));
      }
    }
  }, [correctlyAnsweredQIDs, userProgress.currentStage]);

  const addPoints = (amount) => {
    setQuestPoints(prevPoints => prevPoints + amount);
  };

  const spendPoints = (amount) => {
    if (questPoints >= amount) {
      setQuestPoints(prevPoints => prevPoints - amount);
      return true;
    }
    return false;
  };

  const markQuestionCorrect = (questionId) => {
    setCorrectlyAnsweredQIDs(prev => new Set(prev).add(questionId));
  };

  const buyItem = (item) => {
    if (spendPoints(item.price)) {
      setOwnedItems(prevOwned => [...prevOwned, item.id]);
      return true;
    }
    return false;
  };

  const setAvatar = (avatarId) => {
    localStorage.setItem('userAvatar', avatarId);
    setSelectedAvatar(avatarId);
  };

  const value = { 
    questPoints, 
    addPoints,
    spendPoints,
    ownedItems, 
    buyItem, 
    selectedAvatar, 
    setAvatar,
    userProgress,
    correctlyAnsweredQIDs,
    markQuestionCorrect,
    getQuestionIdsForSaga 
  };

  return <GameContext.Provider value={value}>{children}</GameContext.Provider>;
};
