import React, { useState, useEffect } from 'react';
import sagaData from '../../data/mauryanEmpire.json';
import { useGame } from '../../context/GameContext';
import { Reorder } from 'framer-motion';

const getRandomItem = (arr) => arr[Math.floor(Math.random() * arr.length)];
const shuffleArray = (array) => [...array].sort(() => Math.random() - 0.5);

export default function LegacyLineup({ onGameComplete }) {
  const { addPoints } = useGame();
  const gameData = sagaData.games.legacyLineup;

  const [questionSet, setQuestionSet] = useState(null);
  const [items, setItems] = useState([]);
  const [message, setMessage] = useState('');
  const [isCorrect, setIsCorrect] = useState(null);
  const [timeLeft, setTimeLeft] = useState(120);

  useEffect(() => {
    const randomSet = getRandomItem(gameData.questionBank);
    setQuestionSet(randomSet);
    setItems(shuffleArray(randomSet.rulers));

    const timer = setInterval(() => {
      setTimeLeft(prevTime => {
        if (prevTime <= 1) {
          clearInterval(timer);
          onGameComplete();
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const handleCheckOrder = () => {
    if (!questionSet) return;
    const correctOrder = questionSet.rulers;
    const correct = items.every((item, index) => item.id === correctOrder[index].id);
    setIsCorrect(correct);
    if (correct) {
      const pointsWon = 250 + 250;
      addPoints(pointsWon);
      setMessage(`👑 A perfect succession! +${pointsWon} QP`);
      setTimeout(() => onGameComplete(), 2000);
    } else {
      setMessage('The lineage is incorrect. Try again.');
    }
  };

  if (!questionSet) return <div className="text-center p-8">Loading Lineup...</div>;

  return (
    <div className="w-full max-w-3xl mx-auto bg-white rounded-2xl shadow-xl border border-slate-200">
      <div className="p-6 flex justify-between items-center border-b-2 border-slate-200">
        <div className="text-left">
          <h2 className="text-3xl font-extrabold gradient-text">{questionSet.dynasty}</h2>
          <p className="mt-1 text-slate-600">{gameData.instruction}</p>
        </div>
        <div className="text-center">
            <div className="text-3xl font-bold text-red-500">{timeLeft}</div>
            <div className="text-xs text-slate-500">SECONDS LEFT</div>
        </div>
      </div>

      <div className="p-6 md:p-8">
        <Reorder.Group axis="y" values={items} onReorder={setItems} className="space-y-4">
          {items.map((item, index) => (
            <Reorder.Item 
              key={item.id} 
              value={item}
              className="flex items-center p-4 bg-slate-100 rounded-lg cursor-grab active:cursor-grabbing shadow-sm border border-slate-200"
            >
              <span className="text-lg font-bold text-teal-500 mr-4">{index + 1}.</span>
              <span className="text-slate-800 font-semibold">{item.name}</span>
            </Reorder.Item>
          ))}
        </Reorder.Group>
      </div>

      <div className="p-6 bg-slate-50 rounded-b-2xl">
        {message && <div className={`p-3 rounded-lg text-center mb-4 font-semibold ${isCorrect ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>{isCorrect ? '✅ ' : '❌ '} {message}</div>}
        <button 
          onClick={handleCheckOrder} 
          className="w-full px-8 py-3 rounded-full bg-gradient-to-r from-orange-500 to-red-500 text-white font-bold hover:scale-105 transition-transform shadow-lg shadow-orange-500/20"
        >
          Check Lineup
        </button>
      </div>
    </div>
  );
}
