import React, { useState, useEffect } from 'react';
import sagaData from '../../data/culturalTrivia.json';
import { useGame } from '../../context/GameContext';
import { motion, AnimatePresence } from 'framer-motion';

const getRandomQuestions = (arr, num) => {
  const shuffled = [...arr].sort(() => 0.5 - Math.random());
  return shuffled.slice(0, num);
};

export default function ArtisansEye({ onGameComplete }) {
  const { addPoints } = useGame();
  const gameData = sagaData.games.artisansEye;

  const [gameQuestions, setGameQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [correctAnswers, setCorrectAnswers] = useState(0);
  const [roundScore, setRoundScore] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [isAnswered, setIsAnswered] = useState(false);
  const [timeLeft, setTimeLeft] = useState(15);
  const [isGameOver, setIsGameOver] = useState(false);

  useEffect(() => {
    setGameQuestions(getRandomQuestions(gameData.questionBank, 5));
  }, [gameData.questionBank]);

  const currentQuestion = gameQuestions[currentQuestionIndex];

  const handleNextQuestion = () => {
    const nextQuestionIndex = currentQuestionIndex + 1;
    if (nextQuestionIndex < gameQuestions.length) {
      setCurrentQuestionIndex(nextQuestionIndex);
      setIsAnswered(false);
      setSelectedOption(null);
      setTimeLeft(15);
    } else {
      setIsGameOver(true);
      if (correctAnswers === 5) {
          addPoints(500); // Perfection Bonus
      }
      setTimeout(() => onGameComplete(), 4000);
    }
  };

  useEffect(() => {
    if (isAnswered || isGameOver) return;
    const timer = setInterval(() => {
      setTimeLeft(prevTime => {
        if (prevTime <= 1) {
          handleNextQuestion();
          return 15;
        }
        return prevTime - 1;
      });
    }, 1000);
    return () => clearInterval(timer);
  }, [currentQuestionIndex, isAnswered, isGameOver]);

  const handleOptionClick = (option) => {
    if (isAnswered) return;
    setIsAnswered(true);
    setSelectedOption(option);

    if (option === currentQuestion.answer) {
      setCorrectAnswers(prev => prev + 1);
      const speedBonus = timeLeft * 10;
      const pointsWon = gameData.basePoints + speedBonus;
      setRoundScore(prev => prev + pointsWon);
      addPoints(pointsWon);
    }
    setTimeout(handleNextQuestion, 2000);
  };

  if (isGameOver) {
    return (
      <motion.div initial={{ opacity: 0, scale: 0.8 }} animate={{ opacity: 1, scale: 1 }} className="w-full max-w-2xl mx-auto bg-white rounded-2xl shadow-xl border border-slate-200 text-center p-8">
        <h2 className="text-3xl font-extrabold gradient-text">Round Complete!</h2>
        <p className="text-lg mt-4 text-slate-600">You identified <span className="font-bold">{correctAnswers}</span> out of 5 monuments correctly.</p>
        {correctAnswers === 5 && (
            <motion.p initial={{ scale: 0 }} animate={{ scale: 1 }} transition={{ delay: 0.5, type: 'spring' }} className="text-xl font-bold text-green-500 mt-2">PERFECTION! +500 Bonus QP!</motion.p>
        )}
        <p className="text-5xl font-bold text-slate-800 mt-6">{roundScore + (correctAnswers === 5 ? 500 : 0)}</p>
        <p className="text-slate-500">Total QP Earned This Round</p>
      </motion.div>
    );
  }

  if (!currentQuestion) return <div className="text-center p-8">Loading monuments...</div>;

  const isCorrect = selectedOption === currentQuestion.answer;
  const getButtonStyle = (option) => {
    if (!isAnswered) return 'bg-slate-100 border-slate-200 hover:bg-teal-100 hover:border-teal-400';
    if (option === currentQuestion.answer) return 'bg-green-500 border-green-500 text-white';
    if (option === selectedOption && !isCorrect) return 'bg-red-500 border-red-500 text-white';
    return 'bg-slate-100 border-slate-200 opacity-60';
  };

  return (
    <div className="w-full max-w-2xl mx-auto bg-white rounded-2xl shadow-xl border border-slate-200">
      <div className="p-6 flex justify-between items-center border-b-2 border-slate-200">
        <div className="text-left">
            <h2 className="text-3xl font-extrabold gradient-text">{gameData.title}</h2>
            <p className="mt-1 text-slate-600">Question {currentQuestionIndex + 1} of 5</p>
        </div>
        <div className="text-center">
            <div className="text-3xl font-bold text-red-500">{timeLeft}</div>
            <div className="text-xs text-slate-500">SECONDS LEFT</div>
        </div>
      </div>
      <div className="p-6 md:p-8">
        <AnimatePresence mode="wait">
          <motion.div 
            key={currentQuestionIndex}
            initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} exit={{ opacity: 0, y: -20 }}
            className="text-center mb-6 p-8 bg-slate-800 rounded-lg"
          >
            <p className="text-lg text-slate-300 italic">Identify from this detail:</p>
            <p className="text-2xl text-white font-semibold mt-2">{currentQuestion.image_placeholder}</p>
          </motion.div>
        </AnimatePresence>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {currentQuestion.options.map((option) => (
            <motion.button
              key={option}
              onClick={() => handleOptionClick(option)}
              disabled={isAnswered}
              whileTap={{ scale: 0.95 }}
              className={`p-4 rounded-lg text-lg font-semibold text-center transition-all duration-300 border-2 ${getButtonStyle(option)}`}
            >
              {option}
            </motion.button>
          ))}
        </div>
      </div>
    </div>
  );
}
