import React from "react";

const stats = { username: "TatvaTheExplorer", title: "Level 5: Scribe", lifetimeQP: 1250, sagasMastered: 1, accuracy: "92%" };
const badges = [
  { name: "Mauryan Scholar", desc: "Mastered the Mauryan Empire saga.", emoji: "📜" },
  { name: "Timeline Titan", desc: "Perfect score on a Timeline Jigsaw.", emoji: "⏳" },
  { name: "Quest Novice", desc: "Completed your first quest.", emoji: "✨" },
  { name: "Bronze Collector", desc: "Collected 3 badges.", emoji: "🥉" }
];

const StatCard = ({ label, value, emoji }) => (
  <div className="bg-white p-4 rounded-xl border border-slate-200 text-center shadow-sm transition-all duration-300 ease-in-out hover:shadow-lg hover:scale-105">
    <p className="text-sm text-slate-500 uppercase tracking-wider">{emoji} {label}</p>
    <p className="text-3xl font-bold text-teal-600">{value}</p>
  </div>
);

const Badge = ({ name, desc, emoji }) => (
  <div className="text-center p-4 transition-all duration-300 ease-in-out hover:scale-110">
    <div className="w-20 h-20 mx-auto bg-teal-500 rounded-full flex items-center justify-center shadow-lg shadow-teal-500/20 text-4xl text-white">
      {emoji}
    </div>
    <h4 className="mt-3 font-bold text-slate-800">{name}</h4>
    <p className="text-xs text-slate-500">{desc}</p>
  </div>
);

export default function Profile() {
  return (
    <section>
      <div className="text-center">
        <h2 className="text-4xl font-extrabold gradient-text">@{stats.username}</h2>
        <p className="mt-1 text-lg font-semibold text-slate-600">{stats.title}</p>
      </div>

      <div className="grid grid-cols-2 md:grid-cols-3 gap-4 md:gap-6 mt-8">
        <StatCard label="Lifetime QP" value={stats.lifetimeQP.toLocaleString()} emoji="💎" />
        <StatCard label="Sagas Mastered" value={stats.sagasMastered} emoji="📚" />
        <StatCard label="Accuracy" value={stats.accuracy} emoji="🎯" />
      </div>

      <div className="mt-12">
        <h3 className="text-3xl font-bold text-slate-900 mb-6 text-center">Your Badge Collection</h3>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6 bg-white p-6 rounded-2xl border border-slate-200 shadow-sm">
          {badges.map((b) => <Badge key={b.name} {...b} />)}
        </div>
      </div>
    </section>
  );
}
