import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useGame } from '../context/GameContext';
import storeData from '../data/storeItems.json';

const StoreItemCard = ({ item, onBuy, owned, canAfford }) => (
  <motion.div 
    layout
    className="bg-white/70 backdrop-blur-sm p-6 rounded-2xl border border-slate-200 shadow-md text-center flex flex-col"
  >
    <div className="text-6xl mb-4">{item.emoji}</div>
    <h3 className="text-xl font-bold text-slate-800">{item.name}</h3>
    <p className="text-sm text-slate-600 mt-2 flex-grow">{item.description}</p>
    <div className="mt-6">
      {owned ? (
        <button disabled className="w-full px-6 py-3 rounded-full bg-green-500 text-white font-bold">
          Owned
        </button>
      ) : (
        <button 
          onClick={() => onBuy(item)}
          disabled={!canAfford}
          className={`w-full px-6 py-3 rounded-full font-bold transition-all ${
            canAfford 
              ? 'bg-gradient-to-r from-orange-500 to-red-500 text-white shadow-lg hover:scale-105' 
              : 'bg-slate-200 text-slate-500 cursor-not-allowed'
          }`}
        >
          Buy for {item.price} QP
        </button>
      )}
    </div>
  </motion.div>
);

export default function Store() {
  const { questPoints, spendPoints } = useGame();
  const [ownedItems, setOwnedItems] = useState([]); // In a real app, this would be saved per user

  const handleBuyItem = (item) => {
    if (spendPoints(item.price)) {
      setOwnedItems([...ownedItems, item.id]);
    }
  };

  return (
    <section>
      <div className="text-center">
        <h2 className="text-4xl font-extrabold tracking-tight text-slate-900">Quest Point Store ðŸ’Ž</h2>
        <p className="mt-2 text-lg text-slate-600">Spend your hard-earned points on cosmetic upgrades!</p>
      </div>

      <div className="my-8 p-4 bg-white/50 backdrop-blur-sm rounded-full inline-block mx-auto">
        <p className="font-bold text-lg text-slate-700">Your Balance: <span className="gradient-text">{questPoints} QP</span></p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
        {storeData.items.map(item => (
          <StoreItemCard 
            key={item.id}
            item={item}
            onBuy={handleBuyItem}
            owned={ownedItems.includes(item.id)}
            canAfford={questPoints >= item.price}
          />
        ))}
      </div>
    </section>
  );
}
