import React, { useState, useEffect } from 'react';
import sagaData from '../../data/culturalTrivia.json';
import { useGame } from '../../context/GameContext';

export default function ArtisansEye({ onGameComplete }) {
  const { addPoints } = useGame();
  const gameData = sagaData.games.artisansEye;
  const monument = gameData.monuments[0]; // Using the first monument for now

  const [selectedOption, setSelectedOption] = useState(null);
  const [isAnswered, setIsAnswered] = useState(false);
  const [timeLeft, setTimeLeft] = useState(15);
  const isCorrect = selectedOption === monument.answer;

  useEffect(() => {
    if (isAnswered) return;
    const timer = setInterval(() => {
      setTimeLeft(prevTime => {
        if (prevTime <= 1) {
          clearInterval(timer);
          onGameComplete(); // End game if time runs out
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);
    return () => clearInterval(timer);
  }, [isAnswered, onGameComplete]);

  const handleOptionClick = (option) => {
    if (isAnswered) return;
    setIsAnswered(true);
    setSelectedOption(option);
    if (option === monument.answer) {
      const speedBonus = timeLeft * 10; // 10 points for every second left
      const pointsWon = 150 + speedBonus;
      addPoints(pointsWon);
      // We could add a message here to show points won
    }
    setTimeout(() => onGameComplete(), 2000);
  };

  const getButtonStyle = (option) => {
    if (!isAnswered) return 'bg-slate-100 border-slate-200 hover:bg-teal-100 hover:border-teal-400';
    if (option === monument.answer) return 'bg-green-500 border-green-500 text-white';
    if (option === selectedOption && !isCorrect) return 'bg-red-500 border-red-500 text-white';
    return 'bg-slate-100 border-slate-200 opacity-60';
  };

  return (
    <div className="w-full max-w-2xl mx-auto bg-white rounded-2xl shadow-xl border border-slate-200">
      <div className="p-6 flex justify-between items-center border-b-2 border-slate-200">
        <div className="text-left">
            <h2 className="text-3xl font-extrabold gradient-text">{gameData.title}</h2>
            <p className="mt-1 text-slate-600">{gameData.instruction}</p>
        </div>
        <div className="text-center">
            <div className="text-3xl font-bold text-red-500">{timeLeft}</div>
            <div className="text-xs text-slate-500">SECONDS LEFT</div>
        </div>
      </div>

      <div className="p-6 md:p-8">
        <div className="text-center mb-6 p-8 bg-slate-800 rounded-lg">
          <p className="text-lg text-slate-300 italic">Image Placeholder:</p>
          <p className="text-xl text-white font-semibold mt-2">{monument.image_placeholder}</p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {monument.options.map((option) => (
            <button
              key={option}
              onClick={() => handleOptionClick(option)}
              disabled={isAnswered}
              className={`p-4 rounded-lg text-lg font-semibold text-center transition-all duration-300 border-2 ${getButtonStyle(option)}`}
            >
              {option}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}
