import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Link } from 'react-router-dom';
import mapData from '../data/mapData.json';

const SiteModal = ({ site, onClose }) => (
    <motion.div
      initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}
      className="fixed inset-0 bg-black/60 z-50 flex items-center justify-center p-4"
      onClick={onClose}
    >
      <motion.div
        initial={{ scale: 0.8, y: 50 }} animate={{ scale: 1, y: 0 }} exit={{ scale: 0.8, y: 50 }}
        className="bg-white rounded-2xl shadow-2xl max-w-md w-full text-center p-8 relative"
        onClick={(e) => e.stopPropagation()}
      >
        <h2 className="text-3xl font-extrabold gradient-text">{site.name}</h2>
        <p className="mt-4 text-slate-600">{site.summary}</p>
        <div className="mt-8">
            {site.relatedQuest ? (
                <Link to="/quests" className="px-8 py-3 rounded-full bg-gradient-to-r from-orange-500 to-red-500 text-white font-bold hover:scale-105 transition-transform shadow-lg shadow-orange-500/20">
                    Go to Quest
                </Link>
            ) : (
                <p className="text-sm text-slate-500 italic">More quests related to this site are coming soon!</p>
            )}
        </div>
        <button onClick={onClose} className="mt-4 text-sm text-slate-500 hover:underline">
          Close
        </button>
      </motion.div>
    </motion.div>
);

export default function Explore() {
    const [selectedSite, setSelectedSite] = useState(null);

    return (
        <>
            <section>
                <div className="text-center">
                    <h2 className="text-4xl font-extrabold tracking-tight text-slate-900">The Map Room 🗺️</h2>
                    <p className="mt-2 text-lg text-slate-600">Click on a location to learn more and discover related quests.</p>
                </div>

                <div className="mt-8 p-4 bg-white/70 backdrop-blur-sm rounded-2xl border border-slate-200 shadow-lg">
                    <div className="relative w-full max-w-4xl mx-auto aspect-[4/3]">
                        {/* Simplified SVG Map of India */}
                        <svg className="w-full h-full" viewBox="0 0 450 500">
                            <path d="M211.2,5.2c-1.3-1-3.1-1.2-4.6-0.5c-1.4,0.6-2.4,2-2.4,3.5v22.3l-28.7,30.3c-2.8,3-3.9,7.2-2.8,11.1l21.4,75.2 c-15.6-1.5-33.1,3.4-45.1,15.4c-12,12-16.9,29.6-15.4,45.1L60.5,190.1c-3.9,1.1-7.1,4.3-8.2,8.2L12.1,351.4 c-1.6,5.8,1.3,11.9,7.1,13.5l140.7,39.2c1.8,0.5,3.6,0.5,5.4,0l140.7-39.2c5.8-1.6,8.7-7.7,7.1-13.5L272.8,200.1 c-1.1-3.9-4.3-7.1-8.2-8.2l-73-20.3c1.5-15.6-3.4-33.1-15.4-45.1c-12-12-29.6-16.9-45.1-15.4l21.4-75.2 c1.1-3.9-0.1-8.1-2.8-11.1l-28.7-30.3V8.2c0-1.5-1-2.9-2.4-3.5C214.3,3.9,212.5,4.2,211.2,5.2z" fill="#e0f2f1" stroke="#4db6ac" strokeWidth="2"/>
                        </svg>

                        {/* Interactive Markers */}
                        {mapData.sites.map(site => (
                            <motion.g
                                key={site.id}
                                whileHover={{ scale: 1.5 }}
                                className="cursor-pointer"
                                onClick={() => setSelectedSite(site)}
                            >
                                <circle 
                                    cx={site.coords.x} 
                                    cy={site.coords.y} 
                                    r="8" 
                                    fill="#ef4444" 
                                    stroke="white" 
                                    strokeWidth="2"
                                />
                                <circle 
                                    cx={site.coords.x} 
                                    cy={site.coords.y} 
                                    r="8" 
                                    fill="#ef4444"
                                    stroke="white" 
                                    strokeWidth="2"
                                >
                                    <animate attributeName="r" from="8" to="16" dur="1.5s" begin="0s" repeatCount="indefinite"/>
                                    <animate attributeName="opacity" from="1" to="0" dur="1.5s" begin="0s" repeatCount="indefinite"/>
                                </circle>
                                <text x={site.coords.x + 12} y={site.coords.y + 4} className="font-bold text-sm fill-current text-slate-700 pointer-events-none">
                                    {site.name}
                                </text>
                            </motion.g>
                        ))}
                    </div>
                </div>
            </section>

            <AnimatePresence>
                {selectedSite && (
                    <SiteModal site={selectedSite} onClose={() => setSelectedSite(null)} />
                )}
            </AnimatePresence>
        </>
    );
}
