import React, { useState, useEffect } from 'react';
import sagaData from '../../data/culturalTrivia.json';
import { useGame } from '../../context/GameContext';

export default function FactOrFiction({ onGameComplete }) {
    const { addPoints } = useGame();
    const gameData = sagaData.games.factOrFiction;
    const set = gameData.sets[0]; // Using the first set of statements for now

    const [selected, setSelected] = useState(null);
    const [isAnswered, setIsAnswered] = useState(false);
    const [timeLeft, setTimeLeft] = useState(25);

    useEffect(() => {
      const timer = setInterval(() => {
        setTimeLeft(prevTime => {
          if (isAnswered || prevTime <= 1) {
            clearInterval(timer);
            if (!isAnswered) onGameComplete(); // End game if time runs out without an answer
            return 0;
          }
          return prevTime - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }, [isAnswered, onGameComplete]);

    const handleStatementClick = (statement) => {
        if (isAnswered) return;
        setIsAnswered(true);
        setSelected(statement);
        if (statement.isFiction) {
          const speedBonus = timeLeft * 5; // 5 points for every second left
          const pointsWon = 150 + speedBonus;
          addPoints(pointsWon);
        }
        setTimeout(() => onGameComplete(), 4000); // Longer delay to read explanation
    };

    const getButtonStyle = (statement) => {
      if (!isAnswered) return 'bg-slate-100 border-slate-200 hover:bg-teal-100 hover:border-teal-400';
      // If answered, highlight the FICTION statement in green
      if (statement.isFiction) return 'bg-green-500 border-green-500 text-white';
      // If the user selected a TRUE statement, show it as red
      if (statement === selected && !statement.isFiction) return 'bg-red-500 border-red-500 text-white';
      // Fade out other true statements
      return 'bg-slate-100 border-slate-200 opacity-60';
    };

    return (
        <div className="w-full max-w-3xl mx-auto bg-white rounded-2xl shadow-xl border border-slate-200">
            <div className="p-6 flex justify-between items-center border-b-2 border-slate-200">
                <div className="text-left">
                    <h2 className="text-3xl font-extrabold gradient-text">{gameData.title}</h2>
                    <p className="mt-1 text-slate-600">{gameData.instruction}</p>
                </div>
                <div className="text-center">
                    <div className="text-3xl font-bold text-red-500">{timeLeft}</div>
                    <div className="text-xs text-slate-500">SECONDS LEFT</div>
                </div>
            </div>

            <div className="p-6 md:p-8 space-y-4">
                {set.statements.map((stmt, index) => (
                    <button
                      key={index}
                      onClick={() => handleStatementClick(stmt)}
                      disabled={isAnswered}
                      className={`p-4 rounded-lg w-full text-left text-slate-800 font-medium transition-all duration-300 border-2 ${getButtonStyle(stmt)}`}
                    >
                        <span className="font-bold mr-2">{String.fromCharCode(65 + index)}.</span>{stmt.text}
                    </button>
                ))}
            </div>

            {isAnswered && (
                <div className="p-6 bg-slate-50 rounded-b-2xl">
                    <div className={`p-4 rounded-lg text-center font-semibold ${selected.isFiction ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
                        {selected.isFiction ? `✅ Correct! That statement was fiction.` : `❌ Incorrect. That statement was true.`}
                        {selected.isFiction && set.explanation && <p className="mt-2 text-sm font-normal">{set.explanation}</p>}
                    </div>
                </div>
            )}
        </div>
    );
}
