import React, { useState, useEffect } from 'react';
import sagaData from '../../data/culturalTrivia.json';
import { useGame } from '../../context/GameContext';
import { motion, AnimatePresence } from 'framer-motion';

const getRandomQuestions = (arr, num) => {
  const shuffled = [...arr].sort(() => 0.5 - Math.random());
  return shuffled.slice(0, num);
};

export default function CulturalMatchUp({ onGameComplete }) {
  const { addPoints } = useGame();

  const [gameQuestions, setGameQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [correctAnswers, setCorrectAnswers] = useState(0);
  const [score, setScore] = useState(0);
  const [streak, setStreak] = useState(0);
  const [timeLeft, setTimeLeft] = useState(7);
  const [selectedOption, setSelectedOption] = useState(null);
  const [isAnswered, setIsAnswered] = useState(false);
  const [isGameOver, setIsGameOver] = useState(false);

  useEffect(() => {
    setGameQuestions(getRandomQuestions(sagaData.games.culturalMatchUp.questionBank, 5));
  }, []);

  const currentQuestion = gameQuestions[currentQuestionIndex];

  const handleNextQuestion = () => {
    const nextQuestionIndex = currentQuestionIndex + 1;
    if (nextQuestionIndex < gameQuestions.length) {
      setCurrentQuestionIndex(nextQuestionIndex);
      setIsAnswered(false);
      setSelectedOption(null);
      setTimeLeft(7);
    } else {
      setIsGameOver(true);
      let finalPoints = score;
      if (correctAnswers === 5) {
        finalPoints += 500;
      }
      addPoints(finalPoints);
      setTimeout(() => onGameComplete(), 4000);
    }
  };

  useEffect(() => {
    if (isAnswered || isGameOver) return;
    const timer = setInterval(() => {
      setTimeLeft(prevTime => {
        if (prevTime <= 1) {
          setStreak(0);
          handleNextQuestion();
          return 7;
        }
        return prevTime - 1;
      });
    }, 1000);
    return () => clearInterval(timer);
  }, [currentQuestionIndex, isAnswered, isGameOver]);

  const handleOptionClick = (option) => {
    if (isAnswered) return;
    setIsAnswered(true);
    setSelectedOption(option);

    if (option === currentQuestion.answer) {
      setCorrectAnswers(prev => prev + 1);
      const newStreak = streak + 1;
      const pointsWon = 100 + (newStreak * 10);
      setScore(score + pointsWon);
      setStreak(newStreak);
    } else {
      setStreak(0);
    }

    setTimeout(handleNextQuestion, 1500);
  };

  if (isGameOver) {
    return (
      <motion.div initial={{ opacity: 0, scale: 0.8 }} animate={{ opacity: 1, scale: 1 }} className="w-full max-w-2xl mx-auto bg-white rounded-2xl shadow-xl border border-slate-200 text-center p-8">
        <h2 className="text-3xl font-extrabold gradient-text">Round Complete!</h2>
        <p className="text-lg mt-4 text-slate-600">You answered <span className="font-bold">{correctAnswers}</span> out of 5 correctly.</p>
        {correctAnswers === 5 && (
            <motion.p initial={{ scale: 0 }} animate={{ scale: 1 }} transition={{ delay: 0.5, type: 'spring' }} className="text-xl font-bold text-green-500 mt-2">PERFECTION! +500 Bonus QP!</motion.p>
        )}
        <p className="text-5xl font-bold text-slate-800 mt-6">{score + (correctAnswers === 5 ? 500 : 0)}</p>
        <p className="text-slate-500">Total QP Earned</p>
      </motion.div>
    );
  }

  if (!currentQuestion) return <div className="text-center p-8">Loading questions...</div>;

  const isCorrect = selectedOption === currentQuestion.answer;

  return (
    <div className="w-full max-w-2xl mx-auto bg-white rounded-2xl shadow-xl border border-slate-200 relative overflow-hidden">
      <div 
        className="absolute top-0 left-0 h-2 bg-gradient-to-r from-orange-400 to-red-500 transition-all duration-1000 ease-linear" 
        style={{ width: `${(timeLeft / 7) * 100}%` }}
      ></div>
      <div className="p-6 text-center border-b-2 border-slate-200">
        <h2 className="text-3xl font-extrabold gradient-text">Cultural Match-Up</h2>
        <p className="mt-2 text-slate-600">Question {currentQuestionIndex + 1} of 5</p>
      </div>
      <div className="p-6 md:p-8">
        <AnimatePresence mode="wait">
          <motion.div
            key={currentQuestionIndex}
            initial={{ x: 50, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: -50, opacity: 0 }}
            transition={{ duration: 0.3 }}
            className="text-center mb-6"
          >
            <p className="text-lg text-slate-600">Match this item:</p>
            <h3 className="text-4xl font-bold text-slate-800 mt-2">{currentQuestion.item}</h3>
          </motion.div>
        </AnimatePresence>
        <div className="grid grid-cols-2 gap-4">
          {currentQuestion.options.map((option) => (
            <motion.button
              key={option}
              onClick={() => handleOptionClick(option)}
              disabled={isAnswered}
              whileTap={{ scale: 0.95 }}
              className={`p-4 rounded-lg text-lg font-semibold text-center transition-all duration-300 border-2 
                ${isAnswered && option === currentQuestion.answer ? 'bg-green-500 border-green-500 text-white' : ''}
                ${isAnswered && option === selectedOption && !isCorrect ? 'bg-red-500 border-red-500 text-white' : ''}
                ${!isAnswered ? 'bg-slate-100 border-slate-200 hover:bg-teal-100 hover:border-teal-400' : ''}
              `}
            >
              {option}
            </motion.button>
          ))}
        </div>
      </div>
      <div className="p-4 bg-slate-50 rounded-b-2xl flex justify-between items-center">
        <p className="font-semibold text-slate-700">Score: <span className="text-teal-600">{score}</span></p>
        <p className="font-semibold text-slate-700">Streak: <span className="text-orange-500">{streak}x</span></p>
      </div>
    </div>
  );
}
