import React, { useState, useEffect, useMemo } from 'react';
import sagaData from '../../data/mauryanEmpire.json';
import { useGame } from '../../context/GameContext';
import { motion } from 'framer-motion';
import GameSummary from '../common/GameSummary';

// --- Smart Question Selector ---
const getSmartRandomSet = (questionBank, answeredQIDs) => {
    const unansweredSets = questionBank.filter(set => 
        !set.rulers.every(ruler => answeredQIDs.has(ruler.id))
    );
    if (unansweredSets.length > 0) {
        return unansweredSets[Math.floor(Math.random() * unansweredSets.length)];
    }
    return questionBank[Math.floor(Math.random() * questionBank.length)];
};

const shuffleArray = (array) => [...array].sort(() => Math.random() - 0.5);

const correctSound = new window.Tone.Synth().toDestination();
const wrongSound = new window.Tone.Synth({ oscillator: { type: 'square' } }).toDestination();

export default function LegacyLineup({ onGameComplete }) {
  const { addPoints, correctlyAnsweredQIDs, markQuestionCorrect } = useGame();
  const gameData = sagaData.games.legacyLineup;

  const [questionSet, setQuestionSet] = useState(null);
  const [unsortedItems, setUnsortedItems] = useState([]);
  const [lineup, setLineup] = useState([]);
  const [isGameOver, setIsGameOver] = useState(false);
  const [roundScore, setRoundScore] = useState(0);
  const [isCorrect, setIsCorrect] = useState(null);

  useEffect(() => {
    const set = getSmartRandomSet(gameData.questionBank, correctlyAnsweredQIDs);
    setQuestionSet(set);
    setUnsortedItems(shuffleArray(set.rulers));
    setLineup(Array(set.rulers.length).fill(null));
  }, []);

  const handleDrop = (e, index) => {
    const itemData = JSON.parse(e.dataTransfer.getData("item"));

    // Remove from source
    setUnsortedItems(prev => prev.filter(i => i.id !== itemData.id));

    // Add to target lineup
    const newLineup = [...lineup];

    // If slot is already filled, move that item back to unsorted
    if (newLineup[index]) {
        setUnsortedItems(prev => [...prev, newLineup[index]]);
    }

    newLineup[index] = itemData;
    setLineup(newLineup);
  };

  const handleDragStart = (e, item) => {
    e.dataTransfer.setData("item", JSON.stringify(item));
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleCheckOrder = () => {
    if (!questionSet || lineup.some(item => item === null)) {
        // Optionally, add feedback that all slots must be filled
        return;
    }

    const correct = lineup.every((item, index) => item.id === questionSet.rulers[index].id);
    setIsCorrect(correct);

    if (correct) {
      correctSound.triggerAttackRelease("C4", "8n");
      questionSet.rulers.forEach(ruler => markQuestionCorrect(ruler.id));
      const pointsWon = 30 * questionSet.rulers.length;
      addPoints(pointsWon);
      setRoundScore(pointsWon);
    } else {
      wrongSound.triggerAttackRelease("C2", "8n");
    }
    setIsGameOver(true);
  };

  if (isGameOver) {
    const stats = { 
        score: roundScore, 
        correct: isCorrect ? questionSet.rulers.length : 0, 
        total: questionSet.rulers.length, 
        perfectionBonus: 0 
    };
    return <GameSummary stats={stats} onComplete={onGameComplete} />;
  }

  if (!questionSet) return <div className="text-center p-8">Loading dynasty...</div>;

  return (
    <div className="w-full max-w-4xl mx-auto bg-white rounded-2xl shadow-xl border border-slate-200">
      <div className="p-6 text-center border-b-2 border-slate-200">
        <h2 className="text-3xl font-extrabold gradient-text">{questionSet.dynasty} Lineup</h2>
        <p className="mt-1 text-slate-600">{gameData.instruction}</p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8 p-6 md:p-8">
        {/* Left Column: Unsorted Rulers */}
        <div>
            <h3 className="font-bold text-slate-800 mb-4 text-center">Unsorted Rulers</h3>
            <div className="space-y-3">
                {unsortedItems.map(item => (
                    <motion.div 
                        key={item.id}
                        layout
                        draggable
                        onDragStart={(e) => handleDragStart(e, item)}
                        className="p-4 bg-slate-100 rounded-lg cursor-grab active:cursor-grabbing shadow-sm border border-slate-200 text-center font-semibold text-slate-700"
                    >
                        {item.name}
                    </motion.div>
                ))}
            </div>
        </div>

        {/* Right Column: Your Lineup (Drop Zones) */}
        <div>
            <h3 className="font-bold text-slate-800 mb-4 text-center">Your Lineup</h3>
            <div className="space-y-3">
                {lineup.map((item, index) => (
                    <div 
                        key={index}
                        onDrop={(e) => handleDrop(e, index)}
                        onDragOver={handleDragOver}
                        className="flex items-center p-4 bg-slate-100 rounded-lg border-2 border-dashed border-slate-300 min-h-[60px]"
                    >
                        <span className="text-lg font-bold text-teal-500 mr-4">{index + 1}.</span>
                        {item && (
                            <motion.div 
                                layoutId={item.id}
                                className="w-full bg-white p-2 rounded-md shadow-inner text-center font-semibold text-slate-800"
                            >
                                {item.name}
                            </motion.div>
                        )}
                    </div>
                ))}
            </div>
        </div>
      </div>

      <div className="p-6 bg-slate-50 rounded-b-2xl">
        <button 
          onClick={handleCheckOrder} 
          className="w-full px-8 py-3 rounded-full bg-gradient-to-r from-orange-500 to-red-500 text-white font-bold hover:scale-105 transition-transform shadow-lg shadow-orange-500/20"
        >
          Check Lineup
        </button>
      </div>
    </div>
  );
}
