import React, { createContext, useState, useContext } from 'react';

const GameContext = createContext(null);

export const useGame = () => useContext(GameContext);

export const GameProvider = ({ children }) => {
  const [questPoints, setQuestPoints] = useState(1000); // Start user with 1000 QP

  const addPoints = (amount) => {
    setQuestPoints(prevPoints => prevPoints + amount);
  };

  const spendPoints = (amount) => {
    if (questPoints >= amount) {
      setQuestPoints(prevPoints => prevPoints - amount);
      return true; // Purchase successful
    }
    return false; // Not enough points
  };

  const value = { questPoints, addPoints, spendPoints };

  return <GameContext.Provider value={value}>{children}</GameContext.Provider>;
};
