import React, { useState, useEffect } from 'react';
import sagaData from '../../data/mauryanEmpire.json';
import { useGame } from '../../context/GameContext';

export default function RiddleOfTheSages({ onGameComplete }) {
  const { addPoints } = useGame();
  const gameData = sagaData.games.riddleOfTheSages;
  const riddle = gameData.riddles[0];
  const pointsPerClue = [200, 150, 100];

  const [clueIndex, setClueIndex] = useState(0);
  const [guess, setGuess] = useState('');
  const [message, setMessage] = useState('');
  const [isCorrect, setIsCorrect] = useState(null);
  const [isAnswered, setIsAnswered] = useState(false);
  const [timeLeft, setTimeLeft] = useState(10);

  useEffect(() => {
    if (isAnswered) return;
    const timer = setInterval(() => {
      setTimeLeft(prevTime => {
        if (prevTime <= 1) {
          handleNextClue();
          return 10;
        }
        return prevTime - 1;
      });
    }, 1000);
    return () => clearInterval(timer);
  }, [clueIndex, isAnswered]);

  const handleNextClue = () => {
    if (clueIndex < riddle.clues.length - 1) {
      setClueIndex(clueIndex + 1);
      setTimeLeft(10);
    }
  };

  const handleGuess = (e) => {
    e.preventDefault();
    if (isAnswered) return;
    setIsAnswered(true);

    if (guess.trim().toLowerCase() === riddle.answer.toLowerCase()) {
      const pointsWon = pointsPerClue[clueIndex];
      addPoints(pointsWon);
      setIsCorrect(true);
      setMessage(`✅ Correct! +${pointsWon} QP! The answer is ${riddle.answer}.`);
      setTimeout(() => onGameComplete(), 2500);
    } else {
      setIsCorrect(false);
      setMessage('❌ Not quite. Try again!');
    }
  };

  return (
    <div className="w-full max-w-2xl mx-auto bg-white rounded-2xl shadow-xl border border-slate-200">
      <div className="p-6 text-center border-b-2 border-slate-200">
        <h2 className="text-3xl font-extrabold gradient-text">{gameData.title}</h2>
        <p className="mt-2 text-slate-600">{gameData.instruction}</p>
      </div>

      <div className="p-6 md:p-8">
        <div className="flex justify-between items-center mb-4">
          <h3 className="font-bold text-slate-800">Clue {clueIndex + 1} of {riddle.clues.length}:</h3>
          <p className="text-sm font-semibold text-red-500">Time left: {timeLeft}s</p>
        </div>
        <div className="p-4 bg-slate-100 rounded-lg text-lg text-center italic text-slate-700 min-h-[6rem] flex items-center justify-center">
          "{riddle.clues[clueIndex]}"
        </div>
        {clueIndex < riddle.clues.length - 1 && !isAnswered && (
          <button 
            onClick={handleNextClue} 
            className="w-full mt-4 px-6 py-2 rounded-full bg-teal-100 text-teal-800 font-semibold hover:bg-teal-200 transition"
          >
            Reveal Next Clue
          </button>
        )}
      </div>

      <form onSubmit={handleGuess} className="p-6 bg-slate-50 rounded-b-2xl">
        <div className="flex gap-4">
          <input 
            type="text"
            value={guess}
            onChange={(e) => setGuess(e.target.value)}
            disabled={isAnswered}
            placeholder="Enter your guess..."
            className="flex-grow p-3 rounded-lg border-2 border-slate-300 focus:border-orange-500 focus:ring-0 transition disabled:opacity-70"
          />
          <button 
            type="submit"
            disabled={isAnswered}
            className="px-6 py-3 rounded-lg bg-gradient-to-r from-orange-500 to-red-500 text-white font-bold transition-transform hover:scale-105 disabled:opacity-70 disabled:scale-100"
          >
            Submit
          </button>
        </div>
        {message && (
          <div className={`p-3 mt-4 rounded-lg text-center font-semibold ${isCorrect ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
            {message}
          </div>
        )}
      </form>
    </div>
  );
}
