import React, { useState, useEffect } from 'react';
import sagaData from '../../data/culturalTrivia.json';
import { useGame } from '../../context/GameContext';

export default function CulturalMatchUp({ onGameComplete }) {
  const { addPoints } = useGame();
  // This line is now fixed (camelCase instead of hyphen)
  const gameData = sagaData.games.culturalMatchUp;
  const { questions } = gameData;

  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [streak, setStreak] = useState(0);
  const [timeLeft, setTimeLeft] = useState(7);
  const [selectedOption, setSelectedOption] = useState(null);
  const [isAnswered, setIsAnswered] = useState(false);

  const currentQuestion = questions[currentQuestionIndex];

  const handleNextQuestion = () => {
    const nextQuestionIndex = currentQuestionIndex + 1;
    if (nextQuestionIndex < questions.length) {
      setCurrentQuestionIndex(nextQuestionIndex);
      setIsAnswered(false);
      setSelectedOption(null);
      setTimeLeft(7); // Reset timer for the new question
    } else {
      // Game Over
      onGameComplete();
    }
  };

  useEffect(() => {
    if (isAnswered) return; // Stop the timer if an answer is selected
    const timer = setInterval(() => {
      setTimeLeft(prevTime => {
        if (prevTime <= 1) {
          // Time's up, move to next question, reset streak
          setStreak(0);
          handleNextQuestion();
          return 7;
        }
        return prevTime - 1;
      });
    }, 1000);
    return () => clearInterval(timer);
  }, [currentQuestionIndex, isAnswered]);

  const handleOptionClick = (option) => {
    if (isAnswered) return; // Prevent changing answer

    setIsAnswered(true);
    setSelectedOption(option);

    if (option === currentQuestion.answer) {
      const newStreak = streak + 1;
      const pointsWon = 100 + (newStreak * 10); // Base points + streak bonus
      setScore(score + pointsWon);
      addPoints(pointsWon);
      setStreak(newStreak);
    } else {
      setStreak(0); // Reset streak on wrong answer
    }

    // Move to the next question after a delay
    setTimeout(handleNextQuestion, 1500);
  };

  const isCorrect = selectedOption === currentQuestion.answer;

  return (
    <div className="w-full max-w-2xl mx-auto bg-white rounded-2xl shadow-xl border border-slate-200 relative overflow-hidden">
      <div 
        className="absolute top-0 left-0 h-2 bg-gradient-to-r from-orange-400 to-red-500 transition-all duration-1000 ease-linear" 
        style={{ width: `${(timeLeft / 7) * 100}%` }}
      ></div>

      <div className="p-6 text-center border-b-2 border-slate-200">
        <h2 className="text-3xl font-extrabold gradient-text">{gameData.title}</h2>
        <p className="mt-2 text-slate-600">{gameData.instruction}</p>
      </div>

      <div className="p-6 md:p-8">
        <div className="text-center mb-6">
          <p className="text-lg text-slate-600">Match this item:</p>
          <h3 className="text-4xl font-bold text-slate-800 mt-2">{currentQuestion.item}</h3>
        </div>

        <div className="grid grid-cols-2 gap-4">
          {currentQuestion.options.map((option) => (
            <button
              key={option}
              onClick={() => handleOptionClick(option)}
              disabled={isAnswered}
              className={`p-4 rounded-lg text-lg font-semibold text-center transition-all duration-300 border-2 
                ${isAnswered && option === currentQuestion.answer ? 'bg-green-500 border-green-500 text-white' : ''}
                ${isAnswered && option === selectedOption && !isCorrect ? 'bg-red-500 border-red-500 text-white' : ''}
                ${!isAnswered ? 'bg-slate-100 border-slate-200 hover:bg-teal-100 hover:border-teal-400' : ''}
              `}
            >
              {option}
            </button>
          ))}
        </div>
      </div>

      <div className="p-4 bg-slate-50 rounded-b-2xl flex justify-between items-center">
        <p className="font-semibold text-slate-700">Score: <span className="text-teal-600">{score}</span></p>
        <p className="font-semibold text-slate-700">Streak: <span className="text-orange-500">{streak}x</span></p>
      </div>
    </div>
  );
}
