import React, { useState, useEffect } from 'react';
import sagaData from '../../data/culturalTrivia.json';
import { useGame } from '../../context/GameContext';
import { motion, AnimatePresence } from 'framer-motion';
import GameSummary from '../common/GameSummary';

const getRandomQuestions = (arr, num) => {
  const shuffled = [...arr].sort(() => 0.5 - Math.random());
  return shuffled.slice(0, num);
};
const correctSound = new window.Tone.Synth().toDestination();
const wrongSound = new window.Tone.Synth({ oscillator: { type: 'square' } }).toDestination();

export default function FactOrFiction({ onGameComplete }) {
  const { addPoints } = useGame();
  const gameData = sagaData.games.factOrFiction;

  const [gameQuestions, setGameQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [correctAnswers, setCorrectAnswers] = useState(0);
  const [roundScore, setRoundScore] = useState(0);
  const [selected, setSelected] = useState(null);
  const [isAnswered, setIsAnswered] = useState(false);
  const [timeLeft, setTimeLeft] = useState(25);
  const [isGameOver, setIsGameOver] = useState(false);

  useEffect(() => {
    setGameQuestions(getRandomQuestions(gameData.questionBank, 5));
  }, [gameData.questionBank]);

  const currentSet = gameQuestions[currentQuestionIndex];

  const handleNextQuestion = () => {
    const nextQuestionIndex = currentQuestionIndex + 1;
    if (nextQuestionIndex < gameQuestions.length) {
        setCurrentQuestionIndex(nextQuestionIndex);
        setIsAnswered(false);
        setSelected(null);
        setTimeLeft(25);
    } else {
        setIsGameOver(true);
        if (correctAnswers === 5) {
            addPoints(500);
        }
    }
  };

  useEffect(() => {
    if (isAnswered || isGameOver) return;
    const timer = setInterval(() => {
      setTimeLeft(prevTime => {
        if (prevTime <= 1) {
          handleNextQuestion();
          return 25;
        }
        return prevTime - 1;
      });
    }, 1000);
    return () => clearInterval(timer);
  }, [currentQuestionIndex, isAnswered, isGameOver]);

  const handleStatementClick = (statement) => {
    if (isAnswered) return;
    setIsAnswered(true);
    setSelected(statement);

    if (statement.isFiction) {
      correctSound.triggerAttackRelease("C4", "8n");
      setCorrectAnswers(prev => prev + 1);
      const speedBonus = timeLeft * 5;
      const pointsWon = gameData.basePoints + speedBonus;
      setRoundScore(prev => prev + pointsWon);
      addPoints(pointsWon);
    } else {
      wrongSound.triggerAttackRelease("C2", "8n");
    }
    setTimeout(handleNextQuestion, 3500);
  };

  if (isGameOver) {
    const stats = {
      score: roundScore,
      correct: correctAnswers,
      total: 5,
      perfectionBonus: correctAnswers === 5 ? 500 : 0
    };
    return <GameSummary stats={stats} onComplete={onGameComplete} />;
  }

  if (!currentSet) return <div className="text-center p-8">Loading statements...</div>;

  const getButtonStyle = (statement) => {
    if (!isAnswered) return 'bg-slate-100 border-slate-200 hover:bg-teal-100 hover:border-teal-400';
    if (statement.isFiction) return 'bg-green-500 border-green-500 text-white';
    if (statement === selected && !statement.isFiction) return 'bg-red-500 border-red-500 text-white';
    return 'bg-slate-100 border-slate-200 opacity-60';
  };

  return (
    <div className="w-full max-w-3xl mx-auto bg-white rounded-2xl shadow-xl border border-slate-200">
      <div className="p-6 flex justify-between items-center border-b-2 border-slate-200">
          <div className="text-left">
              <h2 className="text-3xl font-extrabold gradient-text">{gameData.title}</h2>
              <p className="mt-1 text-slate-600">Question {currentQuestionIndex + 1} of 5</p>
          </div>
          <div className="text-center">
              <div className="text-3xl font-bold text-red-500">{timeLeft}</div>
              <div className="text-xs text-slate-500">SECONDS LEFT</div>
          </div>
      </div>
      <AnimatePresence mode="wait">
        <motion.div
            key={currentQuestionIndex}
            initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}
            className="p-6 md:p-8 space-y-4"
        >
          <p className="text-center text-lg text-slate-600 mb-4">Identify the statement that is FICTION.</p>
          {currentSet.statements.map((stmt, index) => (
              <motion.button
                key={index}
                onClick={() => handleStatementClick(stmt)}
                disabled={isAnswered}
                whileTap={{ scale: 0.98 }}
                className={`p-4 rounded-lg w-full text-left text-slate-800 font-medium transition-all duration-300 border-2 ${getButtonStyle(stmt)}`}
              >
                  <span className="font-bold mr-2">{String.fromCharCode(65 + index)}.</span>{stmt.text}
              </motion.button>
          ))}
        </motion.div>
      </AnimatePresence>
    </div>
  );
}
