import React, { useState, useEffect } from 'react';
import sagaData from '../../data/mauryanEmpire.json';
import { useGame } from '../../context/GameContext';
import { motion, AnimatePresence } from 'framer-motion';
import GameSummary from '../common/GameSummary';

const getRandomItem = (arr) => arr[Math.floor(Math.random() * arr.length)];
const correctSound = new window.Tone.Synth().toDestination();
const wrongSound = new window.Tone.Synth({ oscillator: { type: 'square' } }).toDestination();

export default function RiddleOfTheSages({ onGameComplete }) {
  const { addPoints } = useGame();
  const gameData = sagaData.games.riddleOfTheSages;
  const pointsPerClue = [200, 150, 100];

  const [riddle, setRiddle] = useState(null);
  const [clueIndex, setClueIndex] = useState(0);
  const [guess, setGuess] = useState('');
  const [isAnswered, setIsAnswered] = useState(false);
  const [timeLeft, setTimeLeft] = useState(10);
  const [isGameOver, setIsGameOver] = useState(false);
  const [pointsWon, setPointsWon] = useState(0);

  useEffect(() => {
    setRiddle(getRandomItem(gameData.riddles));
  }, [gameData.riddles]);

  useEffect(() => {
    if (isAnswered || !riddle) return;
    const timer = setInterval(() => {
      setTimeLeft(prevTime => {
        if (prevTime <= 1) {
          handleNextClue();
          return 10;
        }
        return prevTime - 1;
      });
    }, 1000);
    return () => clearInterval(timer);
  }, [clueIndex, isAnswered, riddle]);

  const handleNextClue = () => {
    if (riddle && clueIndex < riddle.clues.length - 1) {
      setClueIndex(clueIndex + 1);
      setTimeLeft(10);
    } else {
        setIsGameOver(true);
    }
  };

  const handleGuess = (e) => {
    e.preventDefault();
    if (isAnswered || !riddle) return;
    setIsAnswered(true);

    if (guess.trim().toLowerCase() === riddle.answer.toLowerCase()) {
      correctSound.triggerAttackRelease("C4", "8n");
      const points = pointsPerClue[clueIndex];
      addPoints(points);
      setPointsWon(points);
    } else {
      wrongSound.triggerAttackRelease("C2", "8n");
      setPointsWon(0);
    }
    setTimeout(() => setIsGameOver(true), 2000);
  };

  if (isGameOver) {
    const stats = {
        score: pointsWon,
        correct: pointsWon > 0 ? 1 : 0,
        total: 1,
        perfectionBonus: 0
    };
    return <GameSummary stats={stats} onComplete={onGameComplete} />;
  }

  if (!riddle) return <div className="text-center p-8">Loading riddle...</div>;

  return (
    <div className="w-full max-w-2xl mx-auto bg-white rounded-2xl shadow-xl border border-slate-200">
      <div className="p-6 text-center border-b-2 border-slate-200">
        <h2 className="text-3xl font-extrabold gradient-text">{gameData.title}</h2>
        <p className="mt-2 text-slate-600">{gameData.instruction}</p>
      </div>
      <div className="p-6 md:p-8">
        <div className="flex justify-between items-center mb-4">
          <h3 className="font-bold text-slate-800">Clue {clueIndex + 1} of {riddle.clues.length}:</h3>
          <p className="text-sm font-semibold text-red-500">Time left: {timeLeft}s</p>
        </div>
        <AnimatePresence mode="wait">
            <motion.div
                key={clueIndex}
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -10 }}
                className="p-4 bg-slate-100 rounded-lg text-lg text-center italic text-slate-700 min-h-[6rem] flex items-center justify-center"
            >
                "{riddle.clues[clueIndex]}"
            </motion.div>
        </AnimatePresence>
        {clueIndex < riddle.clues.length - 1 && !isAnswered && (
          <button
            onClick={handleNextClue}
            className="w-full mt-4 px-6 py-2 rounded-full bg-teal-100 text-teal-800 font-semibold hover:bg-teal-200 transition"
          >
            Reveal Next Clue
          </button>
        )}
      </div>
      <form onSubmit={handleGuess} className="p-6 bg-slate-50 rounded-b-2xl">
        <div className="flex gap-4">
          <input
            type="text"
            value={guess}
            onChange={(e) => setGuess(e.target.value)}
            disabled={isAnswered}
            placeholder="Enter your guess..."
            className="flex-grow p-3 rounded-lg border-2 border-slate-300 focus:border-orange-500 focus:ring-0 transition disabled:opacity-70"
          />
          <button
            type="submit"
            disabled={isAnswered}
            className="px-6 py-3 rounded-lg bg-gradient-to-r from-orange-500 to-red-500 text-white font-bold transition-transform hover:scale-105 disabled:opacity-70 disabled:scale-100"
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
}
