import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Link } from 'react-router-dom';
import stagesData from '../data/stagesData.json';

// --- Sub-Components ---

const StageModal = ({ stage, onClose }) => (
    <motion.div
      initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}
      className="fixed inset-0 bg-black/60 z-50 flex items-center justify-center p-4"
      onClick={onClose}
    >
      <motion.div
        initial={{ scale: 0.8, y: 50 }} animate={{ scale: 1, y: 0 }} exit={{ scale: 0.8, y: 50 }}
        className="bg-white rounded-2xl shadow-2xl max-w-md w-full text-center p-8 relative"
        onClick={(e) => e.stopPropagation()}
      >
        <p className="font-semibold text-orange-500">{stage.era}</p>
        <h2 className="text-3xl font-extrabold gradient-text mt-2">{stage.name}</h2>
        <p className="mt-4 text-slate-600">{stage.description}</p>
        <div className="mt-8">
            <Link to="/quests" className="px-8 py-3 rounded-full bg-gradient-to-r from-orange-500 to-red-500 text-white font-bold hover:scale-105 transition-transform shadow-lg shadow-orange-500/20">
                Go to Quests
            </Link>
        </div>
        <button onClick={onClose} className="mt-4 text-sm text-slate-500 hover:underline">
          Close
        </button>
      </motion.div>
    </motion.div>
);

const StageNode = ({ stage, status, onSelect }) => {
    const isLocked = status === 'locked';
    const isCurrent = status === 'current';
    const isCompleted = status === 'completed';

    return (
        <motion.div
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ type: 'spring', delay: Math.random() * 0.5 }}
            whileHover={{ scale: 1.15 }}
            onClick={() => !isLocked && onSelect(stage)}
            className={`relative flex items-center justify-center w-32 h-32 rounded-2xl border-4 p-2 text-center flex-col transition-all duration-300
                ${isLocked ? 'bg-slate-200 border-slate-300 grayscale cursor-not-allowed' : 'bg-white border-teal-500 shadow-lg cursor-pointer'}
                ${isCurrent ? 'shadow-2xl shadow-yellow-400/50 border-yellow-400' : ''}
            `}
        >
            {isLocked && <div className="absolute text-3xl">🔒</div>}
            <h3 className={`font-extrabold text-lg ${isLocked ? 'text-slate-500' : 'text-slate-800'}`}>{stage.name}</h3>
            <p className={`text-xs mt-1 ${isLocked ? 'text-slate-400' : 'text-slate-500'}`}>{stage.era}</p>
            {isCompleted && (
                 <div className="absolute -top-3 -right-3 w-8 h-8 bg-green-500 rounded-full flex items-center justify-center text-white font-bold text-lg shadow-md">✓</div>
            )}
            {isCurrent && (
                <div className="absolute -bottom-4 px-3 py-1 bg-yellow-400 text-yellow-900 text-xs font-bold rounded-full shadow-md">CURRENT</div>
            )}
        </motion.div>
    );
};

// --- Main Page Component ---

export default function Explore() {
    const [selectedStage, setSelectedStage] = useState(null);
    const { stages, userProgress } = stagesData;

    return (
        <>
            <section>
                <div className="text-center">
                    <h2 className="text-4xl font-extrabold tracking-tight text-slate-900">The Explorer's Path 🚂</h2>
                    <p className="mt-2 text-lg text-slate-600">Complete stages on your journey through history to unlock the next chapter.</p>
                </div>

                <div className="mt-12 w-full overflow-x-auto pb-8">
                    <div className="relative flex items-center h-48 min-w-[1200px] px-16">
                        {/* The Track */}
                        <svg className="absolute top-1/2 left-0 w-full h-4 -translate-y-1/2" preserveAspectRatio="none">
                            <path d="M0 2 C 150 2, 150 40, 300 40 S 450 2, 600 2 S 750 40, 900 40 S 1050 2, 1200 2" stroke="#cbd5e1" strokeWidth="6" fill="none" strokeDasharray="10 10"/>
                        </svg>

                        {/* The Stages */}
                        <div className="relative w-full flex justify-between items-center">
                            {stages.map(stage => {
                                let status = 'locked';
                                if (userProgress.completedStages.includes(stage.id)) {
                                    status = 'completed';
                                } else if (userProgress.currentStage === stage.id) {
                                    status = 'current';
                                }

                                return (
                                    <StageNode 
                                        key={stage.id} 
                                        stage={stage} 
                                        status={status} 
                                        onSelect={setSelectedStage} 
                                    />
                                );
                            })}
                        </div>
                    </div>
                </div>
            </section>

            <AnimatePresence>
                {selectedStage && (
                    <StageModal stage={selectedStage} onClose={() => setSelectedStage(null)} />
                )}
            </AnimatePresence>
        </>
    );
}
